mapper.select:
SELECT * FROM p_place 
where 
city=#{city} and gu=#{gu}

SELECT * FROM s_place 
where 
city=#{city} and gu=#{gu}

select * from p_lost l,p_place p 
where 
p.placeid=l.placeid and code=#{code}

select * from p_lost l, p_place p 
where 
p.placeid=l.placeid and p.city=#{city} and p.gu=#{gu} and l.kind=#{kind}

select * from s_lost l, s_place p 
where 
		p.tel=l.tel 
	and 
		p.city=#{city} 
	and 
		p.gu=#{gu} 
	and 
		l.kind=#{kind}
